import { Component, OnInit } from '@angular/core';
import { EventType } from '@angular/router';
import { Observable, BehaviorSubject, map, startWith, catchError, of } from 'rxjs';
import { DataState } from 'src/app/enum/datastate.enum';
import { CustomHttpResponse, Page, Profile } from 'src/app/interface/appstates';
import { State } from 'src/app/interface/state';
import { User } from 'src/app/interface/user';
import { CustomerService } from 'src/app/service/customer.service';
import { UserService } from 'src/app/service/user.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

  homeState$: Observable<State<CustomHttpResponse<Page & User>>>;
  private dataSubject = new BehaviorSubject<CustomHttpResponse<Page & User>>(null);
  private isLoadingSubject = new BehaviorSubject<boolean>(false);
  isLoading$ = this.isLoadingSubject.asObservable();
  private currentPageSubject = new BehaviorSubject<number>(0);
  currentPage$ = this.currentPageSubject.asObservable();
  private showLogsSubject = new BehaviorSubject<boolean>(false);
  showLogs$ = this.showLogsSubject.asObservable();
  readonly DataState = DataState;

  constructor(private userService: UserService, private customerService: CustomerService) {}

  ngOnInit(): void {
    this.homeState$ = this.customerService.customers$()
      .pipe(
        map(response => {
          console.log(response);
          this.dataSubject.next(response);
          return { dataState: DataState.LOADED, appData: response };
        }),
        startWith({ dataState: DataState.LOADING }),
        catchError((error: string) => {
          return of({ dataState: DataState.ERROR, error })
        })
      )
  }

  goToPage(pageNumber?: number): void {
    this.homeState$ = this.customerService.customers$(pageNumber)
      .pipe(
        map(response => {
          console.log(response);
          this.dataSubject.next(response);
          this.currentPageSubject.next(pageNumber);
          return { dataState: DataState.LOADED, appData: response };
        }),
        startWith({ dataState: DataState.LOADED, appData: this.dataSubject.value }),
        catchError((error: string) => {
          return of({ dataState: DataState.LOADED, error, appData: this.dataSubject.value})
        })
      )
  }

  goToNextOrPreviousePage(direction?: string): void {
    this.goToPage(direction === 'forward' ? this.currentPageSubject.value + 1 :this.currentPageSubject.value - 1);
  }

  selectCustomer(customer): void {}

}
<ng-container *ngIf="(homeState$ | async) as state">
  <ng-container>
     <app-navbar [user]="state?.appData?.data?.user"></app-navbar>
     <app-stats [stats]="state?.appData?.data?.stats"></app-stats>
     <section>
       <div class="container">
         <div class="row mt-3">
           <div class="col-12">
             <div class="progress mb-3">
                <div class="progress-bar bg-success" role="progressbar" aria-valuenow="5" aria-valuemin="0" aria-valuemax="100">
                  5%
                </div>
              </div>
           </div>
           <div class="col-6">
              <h4 class="fw-bold text-start">Customers</h4>
           </div>
           <div class="col-6 text-end">
              <i style="cursor: pointer;color: green;font-size: 30px;" class="bi bi-file-earmark-excel"></i>
           </div>
           <div class="col-12">
              <div class="table-responsive">
                <table class="table table-hover">
                  <thead>
                     <tr>
                      <th class="text-center" scope="col">ID</th>
                      <th scope="col">Photo</th>
                      <th scope="col">Name</th>
                      <th scope="col">Email</th>
                      <th scope="col">Phone</th>
                      <th scope="col">Status</th>
                      <th scope="col">Type</th>
                      <th scope="col">Action</th>
                     </tr>
                  </thead>
                  <tbody class="table-body">
                     <tr *ngFor="let customer of state?.appData?.data?.page.content" class="cell-1">
                      <th class="text-center">{{ customer.id }}</th>
                      <td><img [src]="customer.imageUrl" width="42" height="42" class="rounded-circle" [alt]="customer.name"></td>
                      <td>{{ customer.name }}</td>
                      <td>{{ customer.email }}</td>                          
                      <td>{{ customer.phone }}</td>
                      <td>
                         <span class="badge" 
                         [ngClass]="{
                         'bg-success': customer.status === 'ACTIVE',
                         'bg-primary': customer.status === 'PENDING',
                         'bg-danger': customer.status === 'BANNED',
                         'bg-info': customer.status === 'INACTIVE'
                         }">
                           {{ customer.status }}
                         </span>
                      </td>
                      <td>{{ customer.type }}</td>
                      <td>
                         <button type="button" (click)="selectCustomer(customer)" class="btn btn-info btn-sm">View</button>
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>

              <nav *ngIf="state?.appData?.data?.page.content.length > 0" aria-label="Page navigation example">
                <ul class="pagination justify-content-end">
                  <li [ngClass]="0 === (currentPage$ | async) ? 'disabled': '' " class="page-item pointer">
                    <a (click)="goToNextOrPreviousePage('backward')" class="page-link">&laquo; Prev</a>
                  </li>  
                  <li *ngFor="let pageNumber of [].constructor(state?.appData?.data?.page.totalPages); let i = index;" class="page-item pointer">
                    <a (click)=" goToPage(i)" [ngClass]="i === (currentPage$ | async) ? 'active': '' " class="page-link">
                     {{ i + 1 }}
                   </a>
                  </li>
                  <li [ngClass]="(state?.appData?.data?.page.totalPages - 1) === (currentPage$ | async) ? 'disabled': '' "  class="page-item pointer">
                    <a (click)="goToNextOrPreviousePage('forward')" class="page-link">Next &raquo;</a>
                  </li>
                </ul>
              </nav>
           </div>
         </div>
       </div>
     </section>
  </ng-container>
</ng-container>
